import React, { useContext, useState } from "react";
import { Context } from "./Context";

export default function Simon() {
    return (
        <div>
            {currentView === "simon" &&
                <div id="simon_game_container">
                    <div id="upper_simon">
                        <div id="simon_part_yellow"></div>
                        <div id="simon_part_blue"></div>
                    </div>
                    <div id="lower_simon">
                        <div id="simon_part_red"></div>
                        <div id="simon_part_green"></div>
                    </div>
                    <div id="simon_game_info">
                        <input type="submit" onClick={() => alert("play")} class="input_submit" value="&#9654;" />
                        <input type="submit" onClick={() => alert("pause")} class="input_submit" value="&#9208;" />
                    </div>
                </div>
            }
        </div>

    );
}
