import React, { useContext } from "react";
import { Context } from "./Context";

export default function Challenge() {

  const { value1, value9 } = useContext(Context);
  const [challenge, setChallenge] = value9;
  const [socket, setSocket] = value1;

  function sendChallengeAnswer(answer) {
    socket.emit("user_send_response", answer);
    setChallenge(null);
  }

  function shuffleQuestions() {
    let array = challenge.incorrect_answers;
    array.push(challenge.correct_answer);

    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }

    array = array.map((val, key) => { <p onClick={() => sendChallengeAnswer(val)} key={key}>{val}</p> });

    return array;
  }

  return (
    <div>
      {challenge &&
        <div id="challenge">
          <h2>{challenge.question}</h2>
          {shuffleQuestions}
        </div>
      }
    </div>
  );
}
