import React from "react";

import { Context } from "./Context";

function ConnectedUserList(props) {

  const [username, setUsername] = useState("");
  const [currentView, setCurrentView] = useContext(Context);
  const [connectedUserList, setConnectedUserList] = useContext(Context);
  const [currentPrivateChat, setCurrentPrivateChat] = useContext(Context);

  return (
    <div>
      {currentView === "users" &&
        <div>
          <div id="connected_users">
            {connectedUserList.map((val, key) => {
              if (username !== val.username) {
                return (
                  <div key={key} className="connected_user_container" onClick={() => setCurrentPrivateChat(val.username)}>
                    <img
                      src="https://img.utdstc.com/icon/9a8/867/9a8867eb77f8a20e62b5ea69f900de7c650546db544a00ce042d66945fd987bb:200"
                      alt="messenger butterfly icon" />
                    <div>
                      <p>{val.username}</p>
                    </div>
                  </div>
                );
              } else
                return <div key={key}></div>;
            })}
          </div>
        </div>
      }
    </div>
  );
}

export default ConnectedUserList;
